import React from "react";

function Item(props) {

  const { id, title, content, onDelete } = props;

  const deleteItem = (id) => {
    console.log("Deleting note with ID:", id);
    onDelete(id);
  }

  return (
    <div className="item">
      <h2>{title}</h2>
      <p>{content}</p>
      <button onClick={deleteItem}>Delete</button>
    </div>
  );
}

export default Item;
