import React from "react";

function Item(props) {

  const deleteItem = () => {
    console.log(props.id);
    props.onDelete(props.id); // Use props.id to pass the correct ID
  };

  return (
    <div className="item">
      <h2>{props.title}</h2>
      <p>{props.content}</p>
      <button onClick={deleteItem}>Delete</button>
    </div>
  );
}

export default Item;
