import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Item from "./item";
import SideBar from "./sideBar";
import CreateArea from "./createArea";

const initNotes = [
  {
    id: "1",
    type: "Today",
    title: "Study",
    content: "Need to finish three topics",
    user_id: "1",
  },
  {
    id: "2",
    type: "Today",
    title: "Play",
    content: "Need to kill that boss",
    user_id: "1",
  },
  {
    id: "3",
    type: "This Week",
    title: "Project",
    content: "Need to finish the project",
    user_id: "1",
  },
  {
    id: "4",
    type: "This Month",
    title: "Help",
    content: "Need to help three people",
    user_id: "1",
  },
];

function App() {
  const [notes, setNotes] = useState();
  const [title, setTitle] = useState("Today");
  const [showCreateArea, setShowCreateArea] = useState(false);

  useEffect(() => {
    const fetchNotes = async () => {
      const response = await fetch("http://localhost:3000/api/notes", {
        credentials: "include", // Include cookies for session
      });
      const data = await response.json();
      setNotes(data);
    };

    fetchNotes();
  }, []);

  const toggleCreateArea = () => {
    setShowCreateArea(!showCreateArea); // Toggle between true and false
  };

  const addNote = async (newNote) => {
    const response = await fetch("http://localhost:3000/api/notes", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",
      body: JSON.stringify(newNote),
    });
    const addedNote = await response.json();
    setNotes((prevNotes) => [...prevNotes, addedNote]);
  };

  const deleteItems = async (id) => {
    await fetch(`http://localhost:3000/api/notes/${id}`, {
      method: "DELETE",
      credentials: "include",
    });
    setNotes((prevNotes) => prevNotes.filter((note) => note.id !== id));
  };

  return (
    <div className="app">
      <Header Title={title} toggleCreateArea={toggleCreateArea} />
      <div className="main-content">
        <SideBar setTitle={setTitle} />
        {showCreateArea ? (
          <CreateArea addNote={addNote} />
        ) : (
          <div className="items-container">
            {Array.isArray(notes) && notes.length > 0 ? (
              notes
                .filter((noteItem) => noteItem.type === title)
                .map((noteItem) => (
                  <Item
                    key={noteItem.id}
                    id={noteItem.id}
                    title={noteItem.title}
                    content={noteItem.content}
                    onDelete={deleteItems}
                  />
                ))
            ) : (
              <p>No notes available.</p>
            )}
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default App;
